mapper.select:
SELECT CD_UN_KEY as codeKey , CD_GRP_KEY as codeGroupKey , CD_COL_NM as codeColName , CD_NO as codeNum , CD_COMM as codeComment , SUB_CD_GRP_KEY as codeSubGroupKey , CD_NM as codeName FROM TBL_CODE
WHERE CD_GRP_KEY = #{groupKey} and SUB_CD_GRP_KEY IS NOT NULL
WHERE CD_GRP_KEY = #{groupKey}
